const fs = require('fs')
const err = require('./Errors.js')
var oku = (files) => JSON.parse(fs.readFileSync(files,'utf8'))
var yazdir = (files,data) => fs.writeFileSync(files,JSON.stringify(data,null,4))



function add(userData,value) {
    const data = oku('./database.json')
    
    if(isNaN(value)) if(!value) throw err('Ekliyeceğim şeyi belirtmelisin..!')
    if(typeof value != 'number') throw err('Ekliyeceğim şey sayı türünden olmalıdır..!')
    if(data[userData] == undefined) throw err('Böyle bir işlem yapamam çünkü veri kaydedilmemiş..!')
    data[userData] +=value
    yazdir('./database.json',data)

}

module.exports = add