const db = require('../index.js')
const err = require('./Errors.js')




async function arrayHas(arr,val) {
    if(!arr || !val ) throw err('Parametreleri girin...!')
    const data = await db.fetch(arr)
    if(Array.isArray(data) == false ) throw err('Veri Array olmak zorundadÄ±r..!')
    return data.indexOf(val) > -1 ? true : false

}


module.exports = arrayHas