const fs = require('fs')
const oku = (files) => JSON.parse(fs.readFileSync(files))
const err = require('./Errors.js')
var yazdir = (files,data) => fs.writeFileSync(files,JSON.stringify(data,null,4))


 function Delete(way) {
    if(isNaN(way))if(!way) throw err('Neyi silmem gerektigini anlamadım..!')
    const data = oku('./database.json')
    if(!data[way]) throw err('Böyle bir veri yok ki sileyim..!')
    delete data[way]
    yazdir('./database.json',data)

}

module.exports = Delete