const fs = require('fs')
const oku = (files) => JSON.parse(fs.readFileSync(files))
const err = require('./Errors.js')



function get(par) {
    if(!par) throw err('Çekilicek değer belirtilmedi..!')
    const data = oku('./database.json')
   
       return data[par]? data[par] : null
}

module.exports = get