const fs = require('fs')
const err = require('./Errors.js')
var read = (files) => JSON.parse(fs.readFileSync(files,'utf8'))
var print = (files,data) => fs.writeFileSync(files,JSON.stringify(data,null,4))


function push(userData,db) {
    const data = read('./database.json')
    if(Array.isArray(data[userData]) == false) throw err('Girilen değer Array olmak zorundadır..!')
    data[userData].push(db)
    print('./database.json',data)
    
}



module.exports = push