const fs = require('fs')
const err = require('./Errors.js')
var oku = (files) => JSON.parse(fs.readFileSync(files,'utf8'))
var yazdir = (files,data) => fs.writeFileSync(files,JSON.stringify(data,null,4))
try{
    oku('./database.json')
} catch {
    yazdir('./database.json',{})
}

function set(userData,db) {
   // veri kontrolü
    if(!userData) throw err('Tanımlanmayan değer..!')
    if(isNaN(db)) if(!db) throw err('Tanımlanmayan değer..!')
    const data = oku('./database.json')
    data[userData] = db
    yazdir('./database.json',data)
    
}

module.exports = set


