const fs = require('fs')
const read = (files) => JSON.parse(fs.readFileSync(files))
const err = require('./Errors.js')



function startsWith(par) {
    if(!par) throw err('Bulunacak deÄŸer belirtilmedi..!')
    const data = read('./database.json')
    let arr = []
    const res = []
    let keys = Object.keys(data)
    keys.filter(a=>a.startsWith(par))
    .forEach(a=>arr.push(a))
    
    
    arr.forEach(a=>{
        res.push(data[a])
    })
    if(res == '') return null
    else return res
}

module.exports = startsWith